package condicionales;

public class Condicionales1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i = 0; i<=10; i++)
		{
			if (i%2 == 0) {
				System.out.println(i + " PAR");
			}
			else if (i == 5) {
				System.out.println("MITAD");
			}
			else {
				System.out.println(i);
			}
		
		
		
		}
		
		
		
		
		
		
		
		
		
	}

}
